    protected function performPostLoadCallback()
    {
        // echo 'loaded a record ...';

        $currentType = FormUtil::getPassedValue('type', 'user', 'GETPOST', FILTER_SANITIZE_STRING);
        $currentFunc = FormUtil::getPassedValue('func', 'main', 'GETPOST', FILTER_SANITIZE_STRING);

        $this['id'] = (int) ((isset($this['id']) && !empty($this['id'])) ? DataUtil::formatForDisplay($this['id']) : 0);
        $this['userid'] = (int) ((isset($this['userid']) && !empty($this['userid'])) ? DataUtil::formatForDisplay($this['userid']) : 0);
        $this['dbposition'] = (int) ((isset($this['dbposition']) && !empty($this['dbposition'])) ? DataUtil::formatForDisplay($this['dbposition']) : 0);
        $this['block'] = (int) ((isset($this['block']) && !empty($this['block'])) ? DataUtil::formatForDisplay($this['block']) : 0);
    if ($currentFunc != 'edit') {
        $this['plugin'] = ((isset($this['plugin']) && !empty($this['plugin'])) ? DataUtil::formatForDisplayHTML($this['plugin']) : '');
    }
        $this['page'] = (int) ((isset($this['page']) && !empty($this['page'])) ? DataUtil::formatForDisplay($this['page']) : 0);
        $this->prepareItemActions();
        return true;
    }

